function toggleMobileMenu(){const t=document.querySelector(".header__nav");var e=t.querySelector(".menu-button"),n=t.querySelectorAll("A");e.addEventListener("click",function(){t.classList.toggle("header__nav_active"),document.body.classList.toggle("no-scroll")}),n.forEach(e=>{e.addEventListener("click",function(){t.classList.remove("header__nav_active"),document.body.classList.remove("no-scroll")})})}document.addEventListener("DOMContentLoaded",function(){toggleMobileMenu()});